function showRecipe(e){__buildUrl(e,0)}function showIngredient(e){__buildUrl(e,1)}function __buildUrl(e,i){if(window.location.href.includes("search"))switch(i){case 0:window.location.href=window.location.href.replace("search","recipe")+"/"+e;break;case 1:window.location.href=window.location.href.replace("search","ingredient")+"/"+e}else window.location.href=window.location.href+"/"+e}