$(function(){const e=$("#search-input"),t=t=>{e.autocomplete({source:t,minLength:0,select:function(e,t){switch(t.item.type){case"ingredient":return showIngredient(t.item.id),!1;case"recipe":return showRecipe(t.item.id),!1;default:return!1}}}).focus(function(){$(this).autocomplete("search")})};e.keyup(()=>{""!==e.val()&&$.getJSON("http://recetteboisson/api/quick_search/"+e.val(),e=>{t(e)})}),t([])});